package serhatcode.ifelse;

public class ExampleNine {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 0011 =3
		int intVal=100;
		System.out.println("~ result:" + ~intVal);
		
		int intVal2=-100;
		int intVal3=40;
		
		System.out.println(">>> result(negative):" + (intVal2>>>2));
		System.out.println(">>  result(negative):" + (intVal2>>2));
		System.out.println(">>> result: "+ (intVal3>>>2));
		System.out.println(">> result:" + (intVal3>>2));
		
	
	}

}
